mapper.select:
SELECT BOARD_NM
  FROM TB_BOARD
 WHERE BOARD_ID = #{boardId}

mapper.insert:
INSERT INTO TB_POST ( POST_ID
                    , BOARD_ID
                    , USER_ID
                    , POST_TTL
                    , POST_CNT
                    , RGST_ID)
VALUES ( #{postId}
       , #{boardId}
       , #{userId}
       , #{postTtl}
       , #{postCnt}
       , #{rgstId})

mapper.update:
UPDATE TB_POST
   SET POST_TTL = #{postTtl}
     , POST_CNT = #{postCnt}
     , UPDT_DT = NOW()
     , UPDT_ID = #{updtId}
 WHERE POST_ID = #{postId}
