mapper.update:
UPDATE TB_BOARD
   SET BOARD_NM = #{boardNm}
     , UPDT_DT = NOW()
     , UPDT_ID = #{updtId}
 WHERE BOARD_ID = #{boardId}

UPDATE TB_BOARD
   SET DEL_YN = 'Y'
     , UPDT_DT = NOW()
     , UPDT_ID = #{updtId}
 WHERE BOARD_ID = #{boardId}

mapper.insert:
